import React, { useState, useEffect } from 'react';
import { dashboardAPI } from '../services/api';

function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await dashboardAPI.getOverview();
      setDashboardData(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to load dashboard data');
      console.error('Dashboard error:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="card">Loading dashboard...</div>;
  }

  if (error) {
    return <div className="alert alert-error">{error}</div>;
  }

  if (!dashboardData) {
    return <div className="card">No data available</div>;
  }

  return (
    <div>
      <h2>Dashboard Overview</h2>
      
      <div className="grid">
        <div className="stats-card">
          <div className="stats-number">{dashboardData.totalProducts}</div>
          <div>Total Products</div>
        </div>
        
        <div className="stats-card">
          <div className="stats-number">{dashboardData.totalSuppliers}</div>
          <div>Total Suppliers</div>
        </div>
        
        <div className="stats-card">
          <div className="stats-number">${dashboardData.totalInventoryValue}</div>
          <div>Total Inventory Value</div>
        </div>
        
        <div className="stats-card">
          <div className="stats-number">{dashboardData.lowStockProducts}</div>
          <div>Products Running Low</div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
